query BlogPostBySlug(
  $slug: String!
) {
  site {
    siteMetadata {
      title
    }
  }
  post: mdx(fields: {slug: {eq: $slug}}) {
    id
    excerpt
    code {
      body
    }
    frontmatter {
      title
      author
      tags
      date(formatString: "MMMM DD, YYYY")
      description
    }
    wordCount {
      words
    }
  }
}

